mapper.sql:
		1=1

	       and page_id = #{page_id}


	       and template_id = #{template_id}


	       and user_id = #{user_id}


	       and cre_time >= #{cre_time}


	       and upd_time >= #{upd_time}


            and ${_sql_where}


	       and page_id in (select page_id from app_page where app_id=#{app_id})

mapper.update:
update page 
   set page_id=#{page_id}
	  ,template_id=#{template_id}
	  ,page_name=#{page_name}
	  ,user_id=#{user_id}
	  ,cre_time=#{cre_time}
	  ,upd_time=#{upd_time}
 where page_id = #{page_id}

mapper.delete:
        delete from section where section_id in (select section_id from page_section where page_id = #{page_id});
		delete from page where page_id = #{page_id};
		delete from page_section where page_id = #{page_id};
		delete from sec_metric_config where sec_metric_id in (select sec_metric_id from section_metric where section_id = #{page_id});
		delete from section_metric where page_id = #{page_id};
