mapper.select:
SELECT f.cre_time,f.details,f.version_num,f.is_active,j.page_name from ( select sec_metric_id, C.cre_time ,C.metric_names as details ,coalesce(V.version_num, '') as version_num ,coalesce(V.is_active, 0) as is_active from ( -- 该用户所有指标配置历史 select c.sec_metric_id,cre_time, group_concat(distinct m.metric_name) as metric_names from sec_metric_config c, section_metric r, metric m where ( c.user_id = #{user_id} or c.user_id in (select u.user_id from sys_user u, user_role_rela re where u.user_id = re.user_id and re.role_id = 1 ) ) and c.sec_metric_id = r.sec_metric_id
and c.cre_time >= #{start_time}
and c.cre_time <= #{end_time}
and r.metric_id = m.metric_id group by cre_time ) C left outer join ( select cre_time, version_num, is_active -- 该用户设置过的版本号 from user_config_version e where e.user_id = #{user_id}
and e.term_type_id = 0 -- PC ) V on C.cre_time = V.cre_time order by cre_time desc )f left join section_metric h on f.sec_metric_id=h.sec_metric_id left join page_section i on h.section_id=i.section_id left join page j on i.page_id=j.page_id where 1=1
and f.version_num !=''
order by f.cre_time desc

mapper.update:
        update user_config_version set is_active=0 where
		user_id = #{user_id} and term_type_id=#{term_type_id};
		update user_config_version
		set

	      is_active=#{is_active}

		/*0 未激活  1 激活*/
		where
		user_id = #{user_id} and term_type_id=#{term_type_id} and cre_time=#{cre_time}

update user_config_version set
is_active=0
/*0 未激活 1 激活*/ where is_active=1 and user_id = #{user_id} and term_type_id=#{term_type_id}

update user_config_version
set
version_num=#{version_num}
where
user_id = #{user_id} and term_type_id=#{term_type_id} and cre_time=#{cre_time}
