mapper.insert:
{CALL INSERT_USER_ROLE(
	#{role_name, mode=IN},
	#{role_state, mode=IN},
	#{role_type_id, mode=IN},
	#{user_role_predicate, mode=IN},
	#{error_no, jdbcType=INTEGER, mode=OUT}
)}

mapper.update:
{CALL UPDATE_USER_ROLE(
	#{role_id, mode=IN},
	#{role_name, mode=IN},
	#{role_state, mode=IN},
	#{role_type_id, mode=IN},
	#{user_role_predicate, mode=IN},
